import React from 'react';
import { Card, CardImg, CardImgOverlay, CardTitle} from 'reactstrap';

//this is a functional component
//the fingur rule is when we dont need a constractor and state we can use this

    //in the () you can put props and get to props.dish or props.onclick
    function RenderMenuItem({dish, onClick}){
        return (
            <Card key={dish.id}
                onClick={() => onClick(dish.id)}>
                <CardImg width="100%" src={dish.image} alt={dish.name} />
                <CardImgOverlay>
                    <CardTitle>{dish.name}</CardTitle>
                </CardImgOverlay>
            </Card>
        ); 
    }
    const Menu = (props) => {
        
        const menu = props.dishes.map((dish) => {
         return(
            <div  className="col-12 col-md-5 m-1">
                <RenderMenuItem dish={dish} onClick={props.onClick}/>
            </div>
             );
        });
        /*
        dish={dish} onClick={props.onClick}
            does are the parameters that send to the RenderMenuItem function
        */

        return (
            <div className="row">
                {menu}
            </div>
        );
    }

    
    export default Menu;
