import React, {useState} from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Breadcrumb, BreadcrumbItem, Button, Modal, ModalBody, ModalHeader, Label, Col , Row } from 'reactstrap'
import { Control, LocalForm, Errors} from 'react-redux-form';
import Loading from './LoadingComponent';
import { FadeTransform, Fade, Stagger } from 'react-animation-components';
import EditBox from './EditBoxComponent';




function DishDetail(props){

    /*
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [rating, setRating] = useState(5);
    const [author, setAuthor] = useState("");
    const [userComment, setUserComment] = useState("");
    */
    const [isModalOpen, setIsModalOpen] = useState();
    const [formValues, setFormValues] = useState(); // [rating, author, comment]


    if (props.isLoading){
        return ( <Loading/> );
    }
    if(isModalOpen == null){
        setIsModalOpen(false);
        setFormValues([5,"",""]);
    }

    const required = (val) => val && val.length;
    const maxLength = (len) => val => !(val) || (val.length <= len)
    const minLength = (len) => val => !(val) || (val.length >= len)

    function handleSubmit(values){
        setIsModalOpen(! isModalOpen);
        props.postComment(props.dishId, formValues[0], formValues[1], formValues[2]);
        alert("current state is: " + JSON.stringify(values));
    }

    const comments_text = props.comments.map((comment)=>{
        return (
            <Fade in>
                <li className="list-unstyled" key={comment.id}>
                    <p>
                        {comment.comment} <br/>
                        --{comment.author},
                        {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'})
                            .format(new Date(Date.parse(comment.date)))}
                    </p>
                </li>
            </Fade>
        );
        //we use here in a script that translate date to readble date
    });


    try{

    //    const dish = props.myContent.dishes[props.dishId]
        return(
            <div className="container">
                <Breadcrumb>
                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.myContent.dishes[props.dishId].title.text}</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                        <EditBox type="dishes" field="title" id={props.dishId} putContent ={props.putContent} myContent={props.myContent}/>
                    <hr />
                </div>       
                <div className="row">
                    <div className="col-12 col-md-5 m-1">        
                    <FadeTransform in transformProps={{ exitTransform: 'scale(0.5) translateY(-50%)' }}>
                    <Card>
                        <CardImg top src={props.myContent.dishes[props.dishId].image} alt={props.myContent.dishes[props.dishId].title.text} />
                        <CardBody>
                            <CardText> 

                                <EditBox field="description" type="dishes" id={props.dishId} putContent ={props.putContent} myContent={props.myContent}/>
                            </CardText>
                        </CardBody>
                    </Card>
                    </FadeTransform>
                    </div>
                    <div className="col-12 col-md-5 m-1">
                        <h4>comments</h4>
                        <ul className="list-gruop">
                            <Stagger in>
                                {comments_text}
                            </Stagger>
                        </ul>
                        {/*comment*/}
                    <div className="row">
                        <Button outline onClick={setIsModalOpen(! isModalOpen)} className='ml-auto'>
                            <span className='fa fa-pencil fa-lg'></span> Submit Comment
                        </Button>
                    </div>
                    <Modal outline isOpen={isModalOpen} toggle={setIsModalOpen(! isModalOpen)}>
                        <ModalHeader toggle={setIsModalOpen(! isModalOpen)}>Submit Comment</ModalHeader>
                        <ModalBody>

                            <LocalForm onSubmit={(values) => handleSubmit(values)}>

                                <Row className="form-group">
                                    <Col md={{ size: 12 }}>
                                        <Control.select model=".rating" name="rating"
                                            value={formValues[0]} onChange={(event) => setFormValues([event.target.value, formValues[1], formValues[2]])}>
                                            <option>5</option>
                                            <option>4</option>
                                            <option>3</option>
                                            <option>2</option>
                                            <option>1</option>
                                        </Control.select>
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                    <Label htmlFor="author" md={{ size: 12 }}>Your Name</Label>
                                    <Col md={{ size: 12 }}>
                                        <Control.text model=".author" id="author" name="author" placeholder="Your Name" 
                                            className="form-control" onChange={(event) => setFormValues([formValues[0], event.target.value, formValues[2]])}
                                            validators={{required, minLength:minLength(3), maxLength:maxLength(15)}}
                                            value={formValues[1]}/>
                                        <Errors className="text-danger" model=".author"
                                        show="touched" 
                                        messages={{
                                                required: 'Requierd ',
                                                minLength: 'Must be greater than 2 characters ',
                                                maxLength: 'Must be 15 characters or less '
                                        }}>
                                        </Errors>
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                    <Label htmlFor="userComment" md={{ size: 12 }}>Comment</Label>
                                    <Col md={{ size: 12 }}> 
                                        <Control.textarea model=".userComment" id="userComment" name="userComment"
                                            rows="6" className="form-control"  value={formValues[2]}
                                            onChange={(event) => setFormValues([formValues[0], formValues[1], event.target.value])}/>
                                    </Col>
                                </Row>
                                <Row className="form-group">
                                    <Col md={{ size: 12 }}>
                                        <Button type="submit" color="primary">
                                            Submit
                                        </Button>
                                    </Col>
                                </Row>
                            </LocalForm>
                        </ModalBody>
                    </Modal>
                    </div>
                </div>         
            </div>
        );

    }
    catch (e){

        return (
            <div>
                <h4>{props.errMess}{e}</h4>
            </div>
        );
    }
}


export default DishDetail;
