import * as ActionTypes from './ActionTypes';
import { baseUrl } from '../shared/baseUrl';


///////////feedback///////////

export const submitFeedback = ( firstName, lastName, telNum, email, agree, contactType, message) => (dispatch) => {

  const newFeedback = {
    firstName: firstName,
    lastName: lastName,
    telNum: telNum,
    email: email,
    agree: agree,
    contactType: contactType,
    message: message
  };

 //previesdly the id and the date where added in comments.js
  newFeedback.date = new Date().toISOString();

  return fetch(baseUrl + 'feedback', {
      method: "POST",
      body: JSON.stringify(newFeedback),
      headers: {
        "Content-Type": "application/json"
      },
      //related to the backend 
      credentials: "same-origin"
  })
  .then(response => {
      if (response.ok) {
        return response;
      } else {
        var error = new Error('Error ' + response.status + ': ' + response.statusText);
        error.response = response;
        throw error;
      }
    },
    error => {
          throw error;
    })
  .then(response => response.json())
  .then(response => {
    dispatch(addComment(response));
    alert('Your feedback was submited successfully');
  })
  .catch(error =>  { console.log('submmit feedback', error.message);
   alert('Your feedback could not be submited\nError: '+error.message); });
};

export const addFeedback = (feedback) => ({
  type: ActionTypes.ADD_COMMENT,
  payload: feedback
});



///////////comments////////////

//since this is a punc, I need to add the dispatch. So, I'll send the function of a function
export const postComment = (dishId, rating, author, comment) => (dispatch) => {

  const newComment = {
      dishId: dishId,
      rating: rating,
      author: author,
      comment: comment
  };

 //previesdly the id and the date where added in comments.js
  newComment.date = new Date().toISOString();

  return fetch(baseUrl + 'comments', {
      method: "POST",
      body: JSON.stringify(newComment),
      headers: {
        "Content-Type": "application/json"
      },
      //related to the backend 
      credentials: "same-origin"
  })
  .then(response => {
      if (response.ok) {
        return response;
      } else {
        var error = new Error('Error ' + response.status + ': ' + response.statusText);
        error.response = response;
        throw error;
      }
    },
    error => {
          throw error;
    })
  .then(response => response.json())
  .then(response => dispatch(addComment(response)))
  .catch(error =>  { console.log('post comments', error.message); alert('Your comment could not be posted\nError: '+error.message); });
};


export const addComment = (comment) => ({
  type: ActionTypes.ADD_COMMENT,
  payload: comment
});



//////////// LEADERS //////////////

export const fetchLeaders = () => (dispatch) => {
  dispatch(leadersLoading(true));

  return fetch(baseUrl + 'leaders')
  .then(response => {
      if (response.ok) {
        return response;
      } else {
        var error = new Error('Error ' + response.status + ': ' + response.statusText);
        error.response = response;
        throw error;
      }
    },
    error => {
          var errmess = new Error(error.message);
          throw errmess;
    })
  .then(response => response.json())
  .then(leaders => dispatch(addLeaders(leaders)))
  .catch(error => dispatch(leadersFailed(error.message)));

}


export const leadersLoading = () => ({
  type: ActionTypes.LEADERS_LOADING
});

export const leadersFailed = ((errmess) => {
  return({
  type: ActionTypes.LEADERS_FAILED,
  payload: errmess});
});

export const addLeaders = (leaders) => {
  //alert("action creator, leader: "+ JSON.stringify(leaders))
  return({
    type: ActionTypes.ADD_LEADERS,
    payload: leaders
  });
}

