import React, {useState, useEffect, Component} from 'react';
import { Form, Input, Button, Label} from 'reactstrap';
import { Control, Errors} from 'react-redux-form';


const EditBox = (props) => {

    //const [text, setText] = useState("");
    if (props.content === null){
        return <div></div>
    }

    var placeHolder="";
    var type = "text";
    var currentText = ""
    console.log("EditBox: props.content", props.content, " props.feild_name: ", props.field_name)
    switch (props.field_name){
        case "title":
            console.log("EditBox: case title: ", props.content.title)
          //  setText(props.content.title);
          currentText = props.content.title;
            placeHolder="my restrunt name";
            break;

        case "description":
           // setText(props.content.description);
          currentText = props.content.description;
            placeHolder="my restrunt name";
            type = "textarea";
    }



    //handleChange is emplemented in the form itself


    function handleSubmit(event){
      alert(text);
      props.putContent(text, props.field_name);
      event.preventDefault();
    }

    return(
        <Form onSubmit={(event)=> handleSubmit(event)}>
          <Label>
            {props.field_name} 
            <Input type={type} value={text} onChange={(event) => setText(event.target.value)}
                placeholder={placeHolder}/>
            <Button type="submit" value="Submit">submit</Button>
          </Label>
        </Form>
    );


}

export default EditBox;






