import React, { Component } from 'react';
import { Card, CardImg, CardText, CardImgOverlay, CardBody, CardTitle} from 'reactstrap';
import DishDetails from './DishdetailComponent';


class Menu extends Component {
    
    RenderonClick(dishId){
        if(dishId == null){
            return(
                <div></div>
            );
        }
        else{
            return(
                         <div  className="col-12 col-md-5 m-1">
            <Card key={dish.id}
              onClick={() => this.props.onClick(dish.id)}>
              <CardImg width="100%" src={dish.image} alt={dish.name} />
              <CardImgOverlay>
                  <CardTitle>{dish.name}</CardTitle>
              </CardImgOverlay>
            </Card>
          </div>
            );
        }
        
    }

    render() {
        const menu = this.props.dishes.map((dish) => {
            return (
              <div  className="col-12 col-md-5 m-1">
                <Card key={dish.id}
                  onClick={() => this.props.onClick(dish.id)}>
                  <CardImg width="100%" src={dish.image} alt={dish.name} />
                  <CardImgOverlay>
                      <CardTitle>{dish.name}</CardTitle>
                  </CardImgOverlay>
                </Card>
              </div>
            );
        });
        /*
            onClick={() => this.props.onClick(dish.id)
            will send the onClick in the MainComponent the dish id 
        */

        return (
            <div className="container">
            <div className="row">
                {menu}
            </div>
            {this.RenderonClick(this.props.selectedDish)}
            </div>
        );
        /* in the course he deleted the line 
            {this.renderDish(this.state.selectedDish)}
            after the close row div*/
    }
}

export default Menu;
