import React, { Component } from 'react';
// the next comment allow the compiler to ignore the problem
// eslint-disable-next-line
import Menu from './MenuComponent';

import Contact from './ContectComponent';
import Home from './HomeComponent';
import DishDetail from './DishdetailComponent';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import {DISHES} from '../shared/dishes';
import {COMMENTS} from '../shared/comments';
import {LEADERS} from '../shared/leaders';
import {PROMOTIONS} from '../shared/promotions';
import {Switch, Route, Redirect} from 'react-router-dom';



class Main extends Component{

  constructor(props){
    super(props);

    this.state = {
      dishes: DISHES,
      comments: COMMENTS,
      leaders: LEADERS,
      promotions: PROMOTIONS
    };
  }

  render(){

    const HomePage= () => {
      return(
        <Home 
        dish={this.state.dishes.filter((dish) => dish.featured)[0]}
        promotion={this.state.promotions.filter((promo) => promo.featured)[0]}
        leader={this.state.leaders.filter((leader) => leader.featured)[0]}
        />
      );
    }

    return(
        <div>
          <Header/>
          <Switch>
            <Route path='/home' component={HomePage}/>
            <Route exact path='/menu' component={() => <Menu dishes={this.state.dishes}/>}/>
            <Route exact path='/contactus' component={Contact}/>
            <Redirect to="/home"/>
          </Switch>
          <Footer/>
          </div>
    );
    /*
            the "exact" atribute allow only paths that are *exacly as writen*
            if the is no need to pass any props we can simply write:
            <Route exact path='/menu' component={Menu}/>

            Redirect is when nothing works so it will redirect to home


          onClick={(dishId) => this.onDishSelect(dishId)
            this will be activate by the onClick in the MenuComponent

          <DishDetails dish={this.state.dishes.fillter
            ((dish) => dish.id===this.state.selectedDish)}/>
            explained:
            it will send the dishdetails the paramater dish 
            which will be the list that match the conditions
            when the dish id equal to the selected dish
            (remember the selected dish is now only the id and not all the info)
            
            */
  }
}
export default Main;


