import * as ActionTypes from './ActionTypes';
import { baseUrl } from '../shared/baseUrl';

///////////putContnet///////////

export const putContent = (title) => (dispatch) => {

  const newContent = {
    title: title
  };

  return fetch(baseUrl + 'content', {
      method: "POST",
      body: JSON.stringify(newContent),
      headers: {
        "Content-Type": "application/json"
      },
      credentials: "same-origin"
  })
  .then(response => {
      if (response.ok) {
        return response;
      } else {
        var error = new Error('Error ' + response.status + ': ' + response.statusText);
        error.response = response;
        throw error;
      }
    },
    error => {
          throw error;
    })
  .then(response => response.json())
  .then(response => {
    dispatch(updateContent(response));
    alert('Your content was submited successfully');
  })
  .catch(error =>  { console.log('submmit content', error.message);
   alert('Your content could not be submited\nError: '+error.message); });
};

export const updateContent = (content) => ({
  type: ActionTypes.ADD_CONTENT,
  payload: content
});




///////////feedback///////////

export const submitFeedback = ( firstName, lastName, telNum, email, agree, contactType, message) => (dispatch) => {

  const newFeedback = {
    firstName: firstName,
    lastName: lastName,
    telNum: telNum,
    email: email,
    agree: agree,
    contactType: contactType,
    message: message
  };

 //previesdly the id and the date where added in comments.js
  newFeedback.date = new Date().toISOString();

  return fetch(baseUrl + 'feedback', {
      method: "POST",
      body: JSON.stringify(newFeedback),
      headers: {
        "Content-Type": "application/json"
      },
      //related to the backend 
      credentials: "same-origin"
  })
  .then(response => {
      if (response.ok) {
        return response;
      } else {
        var error = new Error('Error ' + response.status + ': ' + response.statusText);
        error.response = response;
        throw error;
      }
    },
    error => {
          throw error;
    })
  .then(response => response.json())
  .then(response => {
    dispatch(addFeedback(response));
    alert('Your feedback was submited successfully');
  })
  .catch(error =>  { console.log('submmit feedback', error.message);
   alert('Your feedback could not be submited\nError: '+error.message); });
};

export const addFeedback = (feedback) => ({
  type: ActionTypes.ADD_COMMENT,
  payload: feedback
});



///////////comments////////////

