import React from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Breadcrumb, BreadcrumbItem, Button, Modal, ModalBody } from 'reactstrap'



    function RenderDish({dish}) {
        if(dish != null){
            return(
                <Card>
                    <CardImg width="100%" top src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            );
        } else{
            return(
                <div></div>
            );
        }

    }
    //when sending var to function you should wrap it with {} un-like in render
    function RenderComments({comments}){
        
        if(comments != null ){
            const comments_text = comments.map((comment)=>{
            return (
                <li className="list-unstyled" key={comment.id}>
                <p>
                    {comment.comment} <br/>
                    --{comment.author},
                    {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'})
                        .format(new Date(Date.parse(comment.date)))}
                </p>
                </li>
            );
            //we use here in a script that translate date to readble date
        });

            return(
                <div className="container">
                    <h4>comments</h4>
                    <ul className="list-gruop">
                        {comments_text}
                    </ul>
                </div>
            )
        } else{
            return(
                <div></div>
            )
        }
    }

    function RenderSubmitComment(){
        return(
            <Modal>
                <ModalBody>

                    <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
                        <Row className="form-group">
                            <Label htmlFor="name" md={2}>yourname Name</Label>
                            <Col md={10}>
                                <Control.text model=".firstname" id="firstname" name="firstname" placeholder="First Name" 
                                    className="form-control" 
                                    validators={{required, minLength:minLength(3), maxLength:maxLength(15)}}/>
                                <Errors className="text-danger" model=".firstname"
                                show="touched" 
                                messages={{
                                        required: 'Requierd ',
                                        minLength: 'Must be greater than 2 characters ',
                                        maxLength: 'Must be 15 characters or less '
                                }}>
                                </Errors>
                            </Col>
                        </Row>
                    </LocalForm>
                </ModalBody>
            </Modal>
        );
    }

    const DishDetail = (props) => {
        
        console.log('componentDidUpdate in render')
      // dish.props allow you to get to a field in another component
      return (
        <div className="container">
            <div className="row">
                <Breadcrumb>

                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>{props.dish.name}</h3>
                    <hr />
                </div>                
            </div>
            <div className="row">
                <div className="col-12 col-md-5 m-1">
                    <RenderDish dish={props.dish} />
                </div>
                <div className="col-12 col-md-5 m-1">
                    <RenderComments comments={props.comments} />
                    <Button onClick={RenderSubmitComment}><span className="fa fa-pencil"/> Submit Comment</Button> 
                    
                </div>
            </div>
        </div>
    );
    }

    
    export default DishDetail;
