import React, { useState } from 'react';
import { Card, CardImg, CardImgOverlay, CardTitle, Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';
import Loading from './Loading'
import { useSelector, useDispatch } from 'react-redux';

//this is a functional component
//the fingur rule is when we dont need a constractor and state we can use this

//in the () you can put props and get to props.dish or props.onclick
function RenderMenuItem({ dish }) {
    return (
        <Card>
            <Link to={`/menu/${dish.id}`}>
                <CardImg width="100%" src={dish.image} alt={dish.title.text} />
                <CardImgOverlay >
                    {dish.title.text}
                </CardImgOverlay>
            </Link>

        </Card>
    );
    //notice that in the Link we use back quots and not regular (bellow the Esc button)
}

const Menu = (props) => {

    const { myContent, isLoading, errMess } = useSelector(store => store.myContent);
    const [render, setRender] = useState(0);

    const AddDish = () => {

        const [showForm, setShowForm] = useState(false);
        const [title, setTitle] = useState();
        const [label, setLabel] = useState();
        const [description, setDescription] = useState();


        function handleSubmit(event) {
            const amount = myContent.staff.length;
            let newEmployee = JSON.parse(JSON.stringify(myContent.staff[amount - 1]));

            newEmployee.title.text = title;
            newEmployee.label.text = label;
            newEmployee.description.text = description;
            newEmployee.id = amount;
            myContent.staff[amount] = newEmployee;
            setShowForm(false);
            dispatch(putContent(myContent));
            setTitle("");
            setLabel("");
            setDescription("");
            setRender(render + 1);
            event.preventDefault()
        }

        return (
            <div>
                <button className="btn btn-primary" style={{ display: showForm ? "none" : "block" }} onClick={() => setShowForm(!showForm)}>add employee</button>

                <Collapse isOpen={showForm} navbar>
                    <form className="col-12 mt-5" onSubmit={(event) => handleSubmit(event)}>
                        <Media tag='li'>
                            <Media left middle>
                                <p>add photo<br />coming soon...</p>
                            </Media>
                            <Media body className="ml-5">
                                <Media heading>
                                    <input value={label} onChange={(event) => setLabel(event.target.value)} name="label" placeholder="staff member job" />
                                </Media>
                                <input value={title} onChange={(event) => setTitle(event.target.value)} name="title" placeholder="staff member name" />
                                <p><input value={description} onChange={(event) => setDescription(event.target.value)} name="description" placeholder="staff member description" /></p>
                            </Media>
                        </Media>
                        <button className="btn btn-light" type="button" onClick={() => setShowForm(!showForm)}>cancel</button>
                        <button className="btn btn-primary" type="submit">add</button>
                    </form>
                </Collapse>
            </div>
        )
    }

    const Dishes = () => {
        if (isLoading) {
            return (
                <div>
                    <div className="col-12 col-md-5 m-1"> <Loading /> </div>
                    <div className="col-12 col-md-5 m-1"> <Loading /> </div>
                </div>
            )
        }
        else {
            return (
                myContent.dishes.map((dish) => {
                    return (
                        <div className="col-12 col-md-5 m-1" key={dish.id}>
                            <Card>
                                <Link to={`/menu/${dish.id}`}>
                                    <CardImg width="100%" src={dish.image} alt={dish.title.text} />
                                    <CardImgOverlay >
                                        {dish.title.text}
                                    </CardImgOverlay>
                                </Link>
                            </Card>
                        </div>
                    );
                }))
        }

    }

    return (
        <div className="container">
            <div className="row">
                <Breadcrumb>
                    <BreadcrumbItem><Link to="/home">Home</Link></BreadcrumbItem>
                    <BreadcrumbItem active>Menu</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>menu</h3> <hr />
                </div>
            </div>
            <div className="row">
                <Dishes/>
            </div>
            <div className="row"><AddDish/></div>
        </div>
    );
}


export default Menu;
