import React, {Component} from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Breadcrumb, BreadcrumbItem,Nav, NavItem, Button, Modal, ModalBody, ModalHeader, Label, Col , Row } from 'reactstrap'
import { Control, LocalForm, Errors} from 'react-redux-form';


const required = (val) => val && val.length;
const maxLength = (len) => val => !(val) || (val.length <= len)
const minLength = (len) => val => !(val) || (val.length >= len)

class DishDetail extends Component{

    constructor(props){
        super(props);

        this.state = {
            isModalOpen: false,
            author: '',
            rating: 5,
            comment: '',
            touched: {
                author: false
            }
        }
        // necessary for using "this" in the callback

        this.handleSubmit = this.handleSubmit.bind(this);
        this.toggleModal = this.toggleModal.bind(this);
     }

    toggleModal() {
        this.setState({ isModalOpen: !this.state.isModalOpen });
    }


    handleSubmit(values){
        this.props.addComment(this.props.dishId, values.rating, values.author, values.comment);
        alert("current state is: " + JSON.stringify(values));
        this.toggleModal();
    }


    render(){

        const RenderDish = (dish) => {
            return(
                <Card>
                    <CardImg width="100%" top src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            );
        }

        const RenderComments = (comments, addComment, dishId) => {
            const comments_text = comments.map((comment)=>{
                return (
                    <li className="list-unstyled" key={comment.id}>
                    <p>
                        {comment.comment} <br/>
                        --{comment.author},
                        {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'})
                            .format(new Date(Date.parse(comment.date)))}
                    </p>
                    </li>
                );
                //we use here in a script that translate date to readble date
            });

            return(
                <div className="container">
                    <h4>comments</h4>
                    <ul className="list-gruop">
                        {comments_text}
                    </ul>
                </div>
            )
            //we use here in a script that translate date to readble date
        }

        return (
            <div className="container">
                <div className="row">
                    <Breadcrumb>
                        <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                        <BreadcrumbItem active>{this.props.dish.name}</BreadcrumbItem>
                    </Breadcrumb>
                    <div className="col-12">
                        <h3>{this.props.dish.name}</h3>
                        <hr />
                    </div>                
                </div>
                <div className="row">
                    <div className="col-12 col-md-5 m-1">
                        {RenderDish(this.props.dish)}
                    </div>
                    <div className="col-12 col-md-5 m-1">
                        {RenderComments(this.props.comments, thisprops.addComment, props.dish.id)}

                    <Nav className='ml-auto'>
                        <NavItem>
                            <Button outline onClick={this.toggleModal}>
                                <span className='fa fa-pencil fa-lg'></span> Submit Comment
                            </Button>
                        </NavItem>
                    </Nav>
                    </div>
                </div>
                <Modal outline isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Submit Comment</ModalHeader>
                    <ModalBody>

                        <LocalForm onSubmit={(values) => this.handleSubmit(values)}>

                            <Row className="form-group">
                                <Col md={{ size: 12 }}>
                                    <Control.select model=".rating" name="rating"
                                            value={this.props.rating} onChange={this.handleInputChange}>
                                        <option>5</option>
                                        <option>4</option>
                                        <option>3</option>
                                        <option>2</option>
                                        <option>1</option>
                                    </Control.select>
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="author" md={{ size: 12 }}>Your Name</Label>
                                <Col md={{ size: 12 }}>
                                    <Control.text model=".author" id="author" name="author" placeholder="Your Name" 
                                        className="form-control" onChange={this.handleInputChange}
                                        validators={{required, minLength:minLength(3), maxLength:maxLength(15)}}
                                        />
                                    <Errors className="text-danger" model=".author"
                                    show="touched" 
                                    messages={{
                                            required: 'Requierd ',
                                            minLength: 'Must be greater than 2 characters ',
                                            maxLength: 'Must be 15 characters or less '
                                    }}>
                                    </Errors>
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Label htmlFor="Comment" md={{ size: 12 }}>Comment</Label>
                                <Col md={{ size: 12 }}> 
                                    <Control.textarea model=".Comment" id="Comment" name="Comment"
                                        rows="6" className="form-control"  onChange={this.handleInputChange}/>
                                </Col>
                            </Row>
                            <Row className="form-group">
                                <Col md={{ size: 12 }}>
                                    <Button type="submit" color="primary">
                                        Submit
                                    </Button>
                                </Col>
                            </Row>
                        </LocalForm>
                    </ModalBody>
                </Modal>
            </div>
        );
    }
}


export default DishDetail;
