import React, {Component, useState } from 'react';
import {Navbar, NavbarBrand, Nav, NavbarToggler, Collapse, NavItem, Jumbotron,
     Button, Modal, ModalHeader, ModalBody, Form, FormGroup, Input, Label} from 'reactstrap'
// nav link auto maticly impliment the active component and a tag
import {NavLink} from 'react-router-dom';
import EditBox from './EditBoxComponent';
import {Tabs, Tab} from "react-bootstrap" ;

const Header = (props) => {

    const [isNavOpen, setIsNavOpen] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalTab, setModalTab] = useState("login");

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [repassword, setRepassword] = useState("");
    const [error, setError] = useState("");

    const [user, setUser] = useState(0);

    


    // setup
    console.log("HeaderComponent: render, content", props.content)
    var content = null;
    var title = ""

    if(props.content !== null){
        console.log("HeaderComponent, if, content", props.content[0])
        content = props.content[0]
        title = props.content[0].title
    }


    function handleLogin(event){
        var tmpError = "";
        
        const userIndex = props.content.findIndex((user) => user.id === "2");
        if(userIndex > 0){
            if(props.content[userIndex].password === password){
                setUser(userIndex)
            }
            else{
                //wrong password
                tmpError = "wrong password or user name";
            }
        }
        else{
            //create new user?
            tmpError = "user name not found\nyou can quickly creat new account in the sign up";
        }

        if(tmpError === ""){
            const fields = [username.value, password.value, props.content.title, props.content.titleFontSize, 
                            props.content.description , props.content.descriptionFontSize];

            
            setError("");
            props.putContent(fields);
            setIsModalOpen(!isModalOpen);
            event.preventDefault();
        }
        else{
            setError(tmpError);
        }


    }

    
  
    return(
        <React.Fragment>
            <Navbar dark expand="md">
                <div className="container">
                    <NavbarToggler onClick={() => setIsNavOpen(!isNavOpen)}/>
                    <NavbarBrand className='mr-auto' href="/">
                        <img src='assets/images/logo.png' height="30" width="41"
                            alt={title}/>
                    </NavbarBrand>
                    <Collapse isOpen={isNavOpen} navbar>
                        <Nav navbar>
                            <NavItem>
                                <NavLink className="nav-link ml-md-5 offset-2 offset-md-0" to="/home">
                                    <span className="fa fa-home"></span> Home
                                </NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link offset-2 offset-md-0" to="/aboutus">
                                    <span className="fa fa-info"></span> About Us
                                </NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link offset-2 offset-md-0" to="/menu">
                                    <span className="fa fa-list"></span> Menu
                                </NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link offset-2 offset-md-0" to="/contactus">
                                    <span className="fa fa-address-card"></span> Contact US
                                </NavLink>
                            </NavItem>
                        </Nav>
                        <Nav className="ml-auto" navbar>
                            <NavItem >
                                <Button outline onClick={() => setIsModalOpen(!isModalOpen)} className="nav-link offset-2 offset-md-0"><span className="fa fa-sign-in"></span> Login</Button>
                            </NavItem>
                        </Nav>
                    </Collapse>
                </div>
            </Navbar>
            <Jumbotron>
                <div className="container">
                    <div className='row row-header'>
                        <div className='col-12 col-sm-6'>
                            <EditBox field_name="title" putContent ={ props.putContent} content={content}/>
                            <EditBox field_name="description" putContent ={ props.putContent} content={content}/>
                        </div>
                    </div>
                </div>
            </Jumbotron>
            <Modal isOpen={isModalOpen} toggle={() => setIsModalOpen(!isModalOpen)}>
                <ModalBody>

                    <Tabs
                    id="controlled-tab-example"
                    activeKey={modalTab}
                    onSelect={(k) => setModalTab(k)}
                    >
                        <Tab eventKey="login" title="login">
                            <Form>
                                <FormGroup>
                                    <Label htmlFor="username">Username</Label>
                                    <Input type="text" id="username" name="username"
                                        innerRef={(input) => setUsername(input)} />
                                </FormGroup>
                                <FormGroup>
                                    <Label htmlFor="password">Password</Label>
                                    <Input type="password" id="password" name="password"
                                        innerRef={(input) => setPassword(input)}  />
                                </FormGroup>
                                <FormGroup check className="col-6">
                                    <Label check>
                                        <Input type="checkbox" name="remember"/>
                                        Remember me
                                    </Label>
                                </FormGroup>
                                <Button className="col-6" onClick={handleLogin} color="primary">Login</Button>
                                <strong>{error}</strong>
                            </Form>

                        </Tab>

                        <Tab eventKey="signUp" title="sign up">
                            <Form onSubmit={handleLogin}>
                                aaaa
                                </Form>
                        </Tab>
                    </Tabs>
                </ModalBody>
            </Modal>
        </React.Fragment>
    );

        /*

                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
                    <ModalBody>
                    <Form onSubmit={this.handleLogin}>
                            <FormGroup>
                                <Label htmlFor="username">Username</Label>
                                <Input type="text" id="username" name="username"
                                    innerRef={(input) => this.username = input} />
                            </FormGroup>
                            <FormGroup>
                                <Label htmlFor="password">Password</Label>
                                <Input type="password" id="password" name="password"
                                    innerRef={(input) => this.password = input}  />
                            </FormGroup>
                            <FormGroup check>
                                <Label check>
                                    <Input type="checkbox" name="remember"
                                    innerRef={(input) => this.remember = input}  />
                                    Remember me
                                </Label>
                            </FormGroup>
                            <Button type="submit" value="submit" color="primary">Login</Button>
                        </Form>
                    </ModalBody>
                </Modal>
        */
    

}

export default Header
