import React, {Component, Fragment} from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Breadcrumb, BreadcrumbItem, Button, Modal, ModalBody, ModalHeader, Label, Col , Row } from 'reactstrap'
import { Control, LocalForm, Errors} from 'react-redux-form';
import Loading from './LoadingComponent';
import { baseUrl } from '../shared/externalUrl';
import { FadeTransform, Fade, Stagger } from 'react-animation-components';
import EditBox from './EditBoxComponent';
import MultiSelect from "react-multi-select-component";
import Dropzone from 'react-dropzone';

const required = (val) => val && val.length;
const maxLength = (len) => val => !(val) || (val.length <= len)
const minLength = (len) => val => !(val) || (val.length >= len)



class CommentForm extends Component{

    constructor(props){
        super(props);

        this.state = {
            isNavOpen: false,
            isModalOpen: false
        };
        // necessary for using "this" in the callback

        this.handleSubmit = this.handleSubmit.bind(this);
        this.toggleModal = this.toggleModal.bind(this);
     }

    toggleModal() {
        this.setState({ isModalOpen: !this.state.isModalOpen });
    }


    handleSubmit(values){
        this.toggleModal();
        this.props.postComment(this.props.id, values.rating, values.author, values.comment);
        alert("current state is: " + JSON.stringify(values));
    }



    render(){     

        const comments_text = (comments) => comments.map((comment)=>{
            return (

                <Fade in>
                    <li className="list-unstyled" key={comment.id}>
                        <p>
                            {comment.comment} <br/>
                            --{comment.author},
                            {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'})
                                .format(new Date(Date.parse(comment.date)))}
                        </p>
                    </li>
                </Fade>
            );
            //we use here in a script that translate date to readble date
        });
        const comments = this.props.comment;
        const postComment = this.props.postComment;
        const id = this.props.id;
        const RenderComments = () => {

            return(
                <div>
                    <h4>comments</h4>
                    <ul className="list-gruop">
                        <Stagger in>
                            {comments_text(comments)}
                        </Stagger>
                    </ul>
                    <CommentForm id={id} postComment={postComment} />

                    <Fragment>
                        <div className="row">
                            <Button outline onClick={this.toggleModal} className='ml-auto'>
                                <span className='fa fa-pencil fa-lg'></span> Submit Comment
                            </Button>
                        </div>
                        <Modal outline isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                            <ModalHeader toggle={this.toggleModal}>Submit Comment</ModalHeader>
                            <ModalBody>

                                <LocalForm onSubmit={(values) => this.handleSubmit(values)}>

                                    <Row className="form-group">
                                        <Col md={{ size: 12 }}>
                                            <Control.select model=".rating" name="rating"
                                                value={this.props.rating}>
                                                <option>5</option>
                                                <option>4</option>
                                                <option>3</option>
                                                <option>2</option>
                                                <option>1</option>
                                            </Control.select>
                                        </Col>
                                    </Row>
                                    <Row className="form-group">
                                        <Label htmlFor="author" md={{ size: 12 }}>Your Name</Label>
                                        <Col md={{ size: 12 }}>
                                            <Control.text model=".author" id="author" name="author" placeholder="Your Name" 
                                                className="form-control" onChange={this.handleInputChange}
                                                validators={{required, minLength:minLength(3), maxLength:maxLength(15)}}
                                                value={this.props.author}/>
                                            <Errors className="text-danger" model=".author"
                                            show="touched" 
                                            messages={{
                                                    required: 'Requierd ',
                                                    minLength: 'Must be greater than 2 characters ',
                                                    maxLength: 'Must be 15 characters or less '
                                            }}>
                                            </Errors>
                                        </Col>
                                    </Row>
                                    <Row className="form-group">
                                        <Label htmlFor="comment" md={{ size: 12 }}>Comment</Label>
                                        <Col md={{ size: 12 }}> 
                                            <Control.textarea model=".comment" id="comment" name="comment"
                                                rows="6" className="form-control"  value={this.props.comment}/>
                                        </Col>
                                    </Row>
                                    <Row className="form-group">
                                        <Col md={{ size: 12 }}>
                                            <Button type="submit" color="primary">
                                                Submit
                                            </Button>
                                        </Col>
                                    </Row>
                                </LocalForm>
                            </ModalBody>
                        </Modal>
                    </Fragment>

                </div>
            )
        }

        return RenderComments
    }
}

