import React, {Component, useState } from 'react';
import { useForm } from "react-hook-form";
import {Navbar, NavbarBrand, Nav, NavbarToggler, Collapse, NavItem, Jumbotron,
     Button, Modal, ModalHeader, ModalBody, Form, FormGroup, Input, Label} from 'reactstrap'
// nav link auto maticly impliment the active component and a tag
import {NavLink} from 'react-router-dom';
import EditBox from './EditBoxComponent';
import {Tabs, Tab} from "react-bootstrap" ;
import initialContent from '../redux/initialContent'
import LogoUrl from '../shared/logoUrl'

const Header = (props) => {

    const [isNavOpen, setIsNavOpen] = useState(false);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalTab, setModalTab] = useState("login");

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [rePassword, setRePassword] = useState("");
    const [loginError, setLoginError] = useState("");
    const [signupError, setSignupError] = useState("");

    const { register, handleSubmit, watch, errors } = useForm();
    const onSubmit = data => {
        console.log(data);
    }
    // TODO set the signup


    // setup
    console.log("HeaderComponent: render, myContent", props.myContent)
    var myContent = null;
    var title = ""

    if(props.myContent === null){
        myContent = initialContent;
    } else{
        myContent = props.myContent;
    }

    function handleLogin(data){
        var tmpError = "";
        setLoginError("");
        
        //const userIndex = props.content.findIndex((user) => user.id === String(username.value));
        const userContent = Object.values(props.content).filter((user) => user.id === data.userName)[0];
        if(userContent){
            
            if(userContent.password === data.password){
                props.fetchMyContent(data.userName)
                setIsModalOpen(!isModalOpen);
            }
            else tmpError = "wrong password or user name";
            
        }
        else tmpError = "user name not found\nyou can easily creat new account in the sign up";

        if(tmpError !== "")  setLoginError(tmpError);
    
    }

    function handleSignup(data){
        var tmpError = "";
        setSignupError("");
        
        if(data.repassword !==  data.password){
            tmpError = "passwords not match\n";
        }
        const userContent = props.content.filter((user) => user.id === data.userName)[0];
        if(userContent !== null){
            tmpError += "user name already exist";
        }
        
        if(tmpError !== ""){
            setSignupError(tmpError);
        }
        else{
            const fields = [data.userName, data.password, myContent.title, myContent.titleFontSize,
                myContent.description , myContent.descriptionFontSize];
            props.signup(fields)
            
            const userContent = props.content.filter((user) => user.id === data.userName)[0];
            props.fetchMyContent(data.userName)


            //intial user 0
        }
    
    }

    
  
    return(
        <React.Fragment>
            <Navbar dark expand="md">
                <div className="container">
                    <NavbarToggler onClick={() => setIsNavOpen(!isNavOpen)}/>
                    <NavbarBrand className='mr-auto' href="/">
                        <img src='assets/images/logo.png' height="30" width="41"
                            alt={title}/>
                    </NavbarBrand>
                    <Collapse isOpen={isNavOpen} navbar>
                        <Nav navbar>
                            <NavItem>
                                <NavLink className="nav-link ml-md-5 offset-2 offset-md-0" to="/home">
                                    <span className="fa fa-home"></span> Home
                                </NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link offset-2 offset-md-0" to="/aboutus">
                                    <span className="fa fa-info"></span> About Us
                                </NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link offset-2 offset-md-0" to="/menu">
                                    <span className="fa fa-list"></span> Menu
                                </NavLink>
                            </NavItem>
                            <NavItem>
                                <NavLink className="nav-link offset-2 offset-md-0" to="/contactus">
                                    <span className="fa fa-address-card"></span> Contact US
                                </NavLink>
                            </NavItem>
                        </Nav>
                        <Nav className="ml-auto" navbar>
                            <NavItem >
                                <Button outline onClick={() => setIsModalOpen(!isModalOpen)} className="nav-link offset-2 offset-md-0"><span className="fa fa-sign-in"></span> Login</Button>
                            </NavItem>
                        </Nav>
                    </Collapse>
                </div>
            </Navbar>
            <Jumbotron>
                <div className="container">
                    <div className='row row-header'>
                        <div className='col-12 col-sm-6'>
                            <EditBox field_name="title" putContent ={ props.putContent} myContent={myContent}/>
                            <EditBox field_name="description" putContent ={ props.putContent} myContent={myContent}/>
                        </div>
                    </div>
                </div>
            </Jumbotron>
            <Modal isOpen={isModalOpen} toggle={() => setIsModalOpen(!isModalOpen)}>
                <ModalBody>

                    <Tabs
                    id="controlled-tab-example"
                    activeKey={modalTab}
                    onSelect={(k) => setModalTab(k)}
                    >
                        <Tab eventKey="login" title="login">
                            <form name="login" onSubmit={handleSubmit(handleLogin)}>
                                <label>user name</label>
                                <input name="userName" ref={register({ required: true })}/>
                                <label>password</label>
                                <input type="password" name="password" ref={register({ required: true })} />
                                {errors.exampleRequired && <span>This field is required</span>}
                                <button type="submit" >login</button>
                            </form>
                        </Tab>
                        <Tab eventKey="signUp" title="sign up">
                            <form name="signup" onSubmit={handleSubmit(handleSignup)}>
                                <label>user name</label>
                                <input name="userName1" ref={register({ required: true })}/>
                                <label>password</label>
                                <input type="password" name="password1" ref={register({ required: true })} />
                                <label>re-enter password</label>
                                <input type="password" name="repassword1" ref={register({ required: true })} />
                                {errors.exampleRequired && <span>This field is required</span>}
                                <button type="submit" >signup</button>
                            </form>

                        </Tab>
                    </Tabs>
                </ModalBody>
            </Modal>
        </React.Fragment>
    );
   /*

   */

}

export default Header
