import React, { useState } from 'react';
import { Card, CardImg, CardImgOverlay, CardTitle, Breadcrumb, BreadcrumbItem } from 'reactstrap';
import { Link } from 'react-router-dom';
import Loading from './Loading'
import { useSelector, useDispatch } from 'react-redux';
import { putContent } from '../redux/ActionCreators';


const Menu = (props) => {

    const { myContent, isLoading, errMess } = useSelector(store => store.myContent);
    const [render, setRender] = useState(0);
    const dispatch = useDispatch()

    const AddDish = () => {

        const [showForm, setShowForm] = useState(false);
        const [title, setTitle] = useState();
        const [selected, setSelected] = useState();
        const [description, setDescription] = useState();

        const options = [
            { label: "Hot ðŸŒ¶", value: "Hot ðŸŒ¶" },
            { label: "Vegan ðŸŒ±", value: "Vegan ðŸŒ±" }
        ]

        function handleSubmit(event) {
            const amount = myContent.staff.length;
            let newEmployee = JSON.parse(JSON.stringify(myContent.staff[amount - 1]));

            newEmployee.title.text = title;
            newEmployee.label = selected.map(label => label.label).join();
            newEmployee.description.text = description;
            newEmployee.id = amount;
            myContent.staff[amount] = newEmployee;
            setShowForm(false);
            dispatch(putContent(myContent));
            setTitle("");
            setLabel("");
            setDescription("");
            setRender(render + 1);
            event.preventDefault()
        }

        return (
            <div>
                <button className="btn btn-primary" style={{ display: showForm ? "none" : "block" }} onClick={() => setShowForm(!showForm)}>add employee</button>

                <Collapse isOpen={showForm} navbar>
                    <form className="col-12 col-md-5 m-1" onSubmit={(event) => handleSubmit(event)}>
                            <Card>
                                <CardTitle>
                                    <input value={title} onChange={(event) => setTitle(event.target.value)} name="title" placeholder="dish name" />
                                </CardTitle>
                                <p>add photo <br />coming soon...</p>
                                <CardBody>
                                    <CardText>
                                        {(selected && selected.length == 2) ? <div>Hot ðŸŒ¶ &amp; Vegan ðŸŒ±</div> : null}
                                        <MultiSelect
                                            options={options}
                                            value={selected}
                                            onChange={(event) => setSelected(Object.values(event))}
                                            labelledBy={"Select"}
                                            selectedValues={selected}
                                        />
                                        <textarea value={description} onChange={(event) => setDescription(event.target.value)} name="description" placeholder="dish description" />
                                    </CardText>
                                </CardBody>
                            </Card>
                        <button className="btn btn-light" type="button" onClick={() => setShowForm(!showForm)}>cancel</button>
                        <button className="btn btn-primary" type="submit">add</button>
                    </form>
                </Collapse>
            </div>
        )
    }

    const Dishes = () => {
        if (isLoading) {
            return (
                <div>
                    <div className="col-12 col-md-5 m-1"> <Loading /> </div>
                    <div className="col-12 col-md-5 m-1"> <Loading /> </div>
                </div>
            )
        }
        else {
            return (
                myContent.dishes.map((dish) => {
                    return (
                        <div className="col-12 col-md-5 m-1" key={dish.id}>
                            <Card>
                                <Link to={`/menu/${dish.id}`}>
                                    <CardImg width="100%" src={dish.image} alt={dish.title.text} />
                                    <CardImgOverlay >
                                        {dish.title.text}
                                    </CardImgOverlay>
                                </Link>
                            </Card>
                        </div>
                    );
                }))
        }

    }

    return (
        <div className="container">
            <div className="row">
                <Breadcrumb>
                    <BreadcrumbItem><Link to="/home">Home</Link></BreadcrumbItem>
                    <BreadcrumbItem active>Menu</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>menu</h3> <hr />
                </div>
            </div>
            <div className="row">
                <Dishes />
            </div>
            <div className="row"><AddDish /></div>
        </div>
    );
}


export default Menu;
