    const [title, setTitle] = useState("");

    function handleSubmit(values){
        console.log("EditBox - values:" , title);
        props.putContent(titletext);

    }

    return(
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <h3>Send us your feedback</h3>
                    <div className="col-12 col-md-9">
                        <Form model="editBox" onSubmit={(values) => handleSubmit(values)}>                                
                            <Row className="form-group">
                                <Col md={10}>
                                    <Control.text model=".text" id="text" name="text" placeholder="First Name" 
                                        className="form-control" onChange={(value) => setTitle(value)}/>
                                </Col>
                            </Row>

                            <Row className="form-group">
                                <Col md={{size: 10, offset: 2}}>
                                    <Button type="submit" color="primary">
                                        Send Feedback
                                    </Button>
                                </Col>
                            </Row>
                        </Form>
                    </div>
                </div>
            </div>
        </div>
    );


}

export default EditBox;






