import React from 'react';
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle} from 'reactstrap';
import {Loading } from './LoadingComponent';
import { baseUrl } from '../shared/baseUrl';

function RenderCard({item, isLoading, errMess}) {
    //alert("HomeComponent- rendercard:\nerrMess: "+ errMess+ " isLoading: "+ isLoading)
    if (errMess != null && errMess !== undefined && errMess.length > 0){
        return(
        <h4>{errMess}</h4>
        );
    }
    else if(isLoading){
       // alert("HomeCOmponent- rendercard: inside *else* if\nerrMess: "+ errMess+ " isLoading: "+ isLoading)
        return(
            <Loading/>
        );
    }
    else if(item === undefined){
        return(
            <Card>
                <CardBody>
                <CardTitle>item is undifine but also there is no error</CardTitle>
                </CardBody>
            </Card>
        );
    }
   // alert("HomeCOmponent- rendercard: inside else else** \nerrMess: "+ errMess+ " isLoading: "+ isLoading)
    return(
        <Card>
        <CardImg src={baseUrl + item.image} alt={item.name} />
            <CardBody>
            <CardTitle>{item.name}</CardTitle>
            {item.designation ? <CardSubtitle>{item.designation}</CardSubtitle> : null}
            <CardText>{item.description}</CardText>
            </CardBody>
        </Card>
    );

}

function Home(props) {
    // in "item={props.dish}" there is no need to write dishes.dishes becouse this is how we send it from the mainComponent
    return(
        <div className="container">
            <div className="row align-items-start">
                <div className="col-12 col-md m-1">
                    <RenderCard item={props.dish} isLoading={props.dishesLoading} errMess={props.dishesErrMess} />
                </div>
                <div className="col-12 col-md m-1">
                    <RenderCard item={props.promotion} isLoading={props.promoLoading} errMess={props.promoErrMess} />
                </div>
                <div className="col-12 col-md m-1">
                    <RenderCard item={props.leader} />
                </div>
            </div>
        </div>
    );
}

export default Home;
