import React, { Component } from 'react';
// the next comment allow the compiler to ignore the problem
// eslint-disable-next-line
import Menu from './MenuComponent';
import Contact from './ContectComponent';
import About from './AboutComponent';
import Home from './HomeComponent';
import DishDetail from './DishdetailComponent';
import Header from './HeaderComponent';
import Footer from './FooterComponent';
import {Switch, Route, Redirect} from 'react-router-dom';
import { connect } from 'react-redux'
import { addComment, fetchDishes } from '../redux/ActionCreators';


const mapStateToProps = state =>{
  return{
    dishes: state.dishes,
    comments: state.comments,
    promotions: state.promotions,
    leaders: state.leaders
  }
}

const mapDispatchToProps = dispatch => ({
  // this allow all the component to get to them     sending it          create a new action and return it
  addComment: (dishId, rating, author, comment) => dispatch(addComment(dishId, rating, author, comment)),
  fetchDishes: () => {dispatch(fetchDishes())}
});

class Main extends Component{

  constructor(props){
    super(props);

    
  }

  componentDidMount(){
    this.props.fetchDishes();
  }


  render(){



    const HomePage = () => {
      return(
          <Home 
              dish={this.props.dishes.dishes.filter((dish) => dish.featured)[0]}
              dishesLoading={this.props.dishes.isLoading}
              dishesErrMess={this.props.dishes.errMess}
              promotion={this.props.promotions.filter((promo) => promo.featured)[0]}
              leader={this.props.leaders.filter((leader) => leader.featured)[0]}
          />
      );
    }

/*
    const HomePage= () => {
      return(
        <Home 
        //becouse the dishes object has cahnged (in dishes.js) and containing several fields, one of the is dishes so we use dishes.dishes
        dish={this.props.dishes.dishes.filter((dish) => dish.featured)[0]}
        dishesLoading={this.props.dishes.isLoading}
        dishesErrMess={this.props.dishes.errmess}
        promotion={this.props.promotions.filter((promo) => promo.featured)[0]}
        leader={this.props.leaders.filter((leader) => leader.featured)[0]}
        />
      );
      //here we changed from this.state.XXX to this.props.XXX
    }
/*

    const DishWithId = ({match}) => {
      return(
          <DishDetail dish={this.props.dishes.dishes.filter((dish) => dish.id === parseInt(match.params.dishId,10))[0]}
            isLoading={this.props.dishes.isLoading}
            errMess={this.props.dishes.errMess}
            comments={this.props.comments.filter((comment) => comment.dishId === parseInt(match.params.dishId,10))}
            addComment={this.props.addComment}
          />
      );
    };

/*
    //this allow the DishDetail class in dishdetailComponent
    const DishWithId = ({match}) => {
      return(
        <DishDetail 
        dish={this.props.dishes.dishes.filter((dish)  => dish.id === parseInt(match.params.dishId, 10))[0]}
        // although the following is the same as the above dishesLoading and dishesErrMess, here we only passing the deishes 
        //unlike in the above where we also senf the promotions and leaders
        isLoading={this.props.dishes.isLoading}
        ErrMess={this.props.dishes.errmess}
        comments={this.props.comments.filter((comment) => comment.dishId === parseInt(match.params.dishId, 10))}
        addComment={this.props.addComment}
        />
      );
      // the 10 in parseInt will turn the string number to be in base 10 
      //in the first we use [0] to send only one dish not as Array
      //in the second we want to send all the comments for this dish as array
    }
*/
    return(
        <div>
          <Header/>
          <Switch>
            <Route path='/home' component={HomePage}/>
            <Route exact path='/menu' component={() => <Menu dishes={this.props.dishes}/>}/>
            <Route path='/menu/:dishId' component={DishWithId}/>
            <Route exact path='/contactus' component={Contact}/>
            <Route exact path='/aboutus' component={() => <About leaders={this.props.leaders}/>}/>
            <Redirect to="/home"/>
          </Switch>
          <Footer/>
          </div>
    );
  }
}
// this connect the main to the redux store and allow accsess to its fields
export default (connect(mapStateToProps, mapDispatchToProps)(Main));
