    .then(status)
    .then(res => res.json())
    .catch(function(error) {
        alert("Error", error);
    });

    function status(response) {   
        if (response.ok) {
            return response;
        }
        return response.json().then(res => Promise.reject(res));
    }
}

// this is the way to return a function
