import React from 'react';
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle} from 'reactstrap';
import Loading  from './LoadingComponent';
import { FadeTransform } from 'react-animation-components';

import {useSelector} from 'react-redux'


function Home(props) {

    const RenderCard = ({item}) => {
        if(item === undefined){
            return(
                <Card>
                    <CardBody>
                    <CardTitle>item is undifine but also there is no error</CardTitle>
                    </CardBody>
                </Card>
            );
        }
        return(
            <FadeTransform in transformProps={{exitTransform: 'scale(0.5) translateY(-50%)' }}>
                <Card>
                    <CardImg src={item.image} alt={item.title.text} />
                    <CardBody>
                    <CardTitle>{item.title.text}</CardTitle>
                    {item.designation ? <CardSubtitle>{item.designation}</CardSubtitle> : null }
                    <CardText>{item.description.text}</CardText>
                    </CardBody>
                </Card>
            </FadeTransform>
        );
    }

    const {myContent, isLoading, errMess} = useSelector(store => store.myContent);

    if(errMess !== null && errMess !== undefined){
        return (<div>{errMess}</div>)
    }

    return(
        <div className="container">
            <div className="row align-items-start">
                <div className="col-12 col-md m-1">
                    {isLoading? <Loading /> : <RenderCard item={myContent.dishes[0]}/>}
                </div>
                <div className="col-12 col-md m-1">
                    {isLoading? <Loading /> : <RenderCard item={myContent.thePlace[0]}/>}
                </div>
                <div className="col-12 col-md m-1">
                    {isLoading? <Loading /> : <RenderCard item={myContent.staff[0]}/>}
                </div>
            </div>
        </div>
    );
}

export default Home;
