import React, {useState, useEffect} from 'react';
import { Form, Input, Button, Label} from 'reactstrap';
import '../App.css';
import styles from  './aaaa.module.css';


const EditBox = (props) => {

    const [text, setText] = useState(null);
    const [edit, setEdit] = useState(false);
    const [fontSize, setFontSize] = useState("40px");

    if (props.content === null){
        return <div></div>
    }

    var field_index = 0;
    var placeHolder="";
    var type = "text";
    //var currentText = ""
    console.log("EditBox: props.content", JSON.stringify(props.content), " props.feild_name: ", props.field_name)
    switch (props.field_name){
        case "title":
            if (text === null){
                setText(props.content.title)
                setFontSize(props.content.titleFontSize);
            }
            placeHolder="restrunt name";
            break;

        case "description":
            if (text === null){
                setText(props.content.description);
                setFontSize(props.content.descriptionFontSize);
            }
            field_index = 1;
            placeHolder="restrunt description";
            type = "textarea";
    }


    function handleChange(event){        
        console.log("EditBox: event.value: ", event.target.value);
        setText(event.target.value);
        console.log("EditBox: text: ", text);
    }


    function handleSubmit(event){
        var fields = [props.content.title, props.content.description];
        fields[field_index] = text;
        console.log(fields);
        props.putContent(fields);
        setEdit(false)
        event.preventDefault();
    }
    function handleOnClick(){
        setEdit(true);
    }
    function handleFontSize(amount){
        setFontSize(fontSize+amount);
    }


    if(edit){
        return(
            <Form onSubmit={(event)=> handleSubmit(event)}>
                <Input  style={{fontSize:fontSize}} type={type} value={text} onChange={(event) => handleChange(event)}
                    placeholder={placeHolder}/>
                <Button type="submit" className={styles.EditBoxButton}>save</Button>
            </Form>
        );
        //<Button type="submit" value="Submit" className='editBoxButton'>save</Button>
    }
    else{

        return(
            <div>
                <div className="row"  style={{ padding: "10px"}}>
                <span className="fa fa-angle-down"></span>
                    <div className="col-6" >
                        <div  className="row" style={{fontSize:fontSize, mb:0}}>{text}</div> 
                        <div  className="row" >
                            <Button style={{height: '22px', width : '50px', fontSize:'14px', padding:'0px'}}
                                  onClick={()=>handleOnClick()}>edit</Button>
                            <Button style={{height: '22px', width : '22px', fontSize:'14px', padding:'0px'}}
                                  onClick={()=>handleFontSize(+2)}>
                                <span className="fa fa-angle-up"></span></Button>
                            <Button style={{height: '22px', width : '22px', padding:'0px'}}
                                    onClick={()=>handleFontSize(-2)}>
                                <span className="fa fa-angle-down"></span></Button>
                        </div>
                    </div>
                </div>
            </div>

            /*

            <div>
                <div className="row">
                    <div className="col-6" >
                        <div style={{fontSize:fontSize}}>{text}</div> <br/>
                        <Button style={{height: '30px', width : '80px', fontSize:'14px'}}  onClick={()=>handleOnClick()} value="Submit">edit</Button>
                    </div>
                </div>
            </div>
            */
        );
    }

}

export default EditBox;






