import React, {Component} from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Breadcrumb, BreadcrumbItem, Button, Modal, ModalBody, ModalHeader, Label, Col , Row } from 'reactstrap'
import { Control, LocalForm, Errors} from 'react-redux-form';


const required = (val) => val && val.length;
const maxLength = (len) => val => !(val) || (val.length <= len)
const minLength = (len) => val => !(val) || (val.length >= len)

class DishDetail extends Component{

    constructor(props){
        super(props);

        this.state = {
            isModalOpen: false,
            name: '',
            rate: 5,
            comment: '',
            touched: {
                name: false, 
                rate: false, 
                comment: false
            }
        }
        // necessary for using "this" in the callback

        this.handleSubmit = this.handleSubmit.bind(this);
     }


    toggleModal() {
        this.setState({
          isModalOpen: !this.state.isModalOpen
        });
      }


    handleSubmit(values){
        console.log("current state is: " + JSON.stringify(values));
        alert("current state is: " + JSON.stringify(values));
    }


    render(){

        const RenderDish = (dish) => {
            return(
                <Card>
                    <CardImg width="100%" top src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            );
        }

        const RenderComments = (comment) => {
            return (
                <div className="container">
                    <h4>comments</h4>
                    <ul className="list-gruop">
                        <li className="list-unstyled" key={comment.id}>
                        <p>
                            {comment.comment} <br/>
                            --{comment.author},
                            {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'})
                                .format(new Date(Date.parse(comment.date)))}
                        </p>
                        </li>
                    </ul>
                </div>
            );
            //we use here in a script that translate date to readble date
        }

        return (
            <div className="container">
                <div className="row">
                    <Breadcrumb>

                        <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                        <BreadcrumbItem active>{this.props.dish.name}</BreadcrumbItem>
                    </Breadcrumb>
                    <div className="col-12">
                        <h3>{this.props.dish.name}</h3>
                        <hr />
                    </div>                
                </div>
                <div className="row">
                    <div className="col-12 col-md-5 m-1">
                        {RenderDish(this.props.dish)}
                    </div>
                    <div className="col-12 col-md-5 m-1">
                        {RenderComments(this.props.comments)}
                        <Button onClick={this.handleSubmit}><span className="fa fa-pencil"/> Submit Comment</Button> 

                    </div>
                </div>
                <Modal isOpen={this.props.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Submit Comment</ModalHeader>
                <ModalBody>

                    <LocalForm onSubmit={(values) => this.handleSubmit(values)}>

                        <Row className="form-group">
                            <Col>
                                <Control.select model=".rate" name="rate"
                                        value={this.props.rate} onChange={this.handleInputChange}>
                                    <option>5</option>
                                    <option>4</option>
                                    <option>3</option>
                                    <option>2</option>
                                    <option>1</option>
                                </Control.select>
                            </Col>
                        </Row>
                        <Row className="form-group">
                            <Label htmlFor="name">Your Name</Label>
                            <Col>
                                <Control.text model=".name" id="name" name="name" placeholder="Your Name" 
                                    className="form-control" 
                                    validators={{required, minLength:minLength(3), maxLength:maxLength(15)}}/>
                                <Errors className="text-danger" model=".firstname"
                                show="touched" 
                                messages={{
                                        required: 'Requierd ',
                                        minLength: 'Must be greater than 2 characters ',
                                        maxLength: 'Must be 15 characters or less '
                                }}>
                                </Errors>
                            </Col>
                        </Row>
                        <Row className="form-group">
                            <Label htmlFor="Comment">Comment</Label>
                            <Col>
                                <Control.textarea model=".Comment" id="Comment" name="Comment"
                                    rows="6" className="form-control" />
                            </Col>
                        </Row>
                    </LocalForm>
                </ModalBody>
            </Modal>
            </div>
        );
    }
}


export default DishDetail;
