import React, {useState} from 'react';
import Loading  from './LoadingComponent'


const EditBox = (props) => {

    const [text, setText] = useState(null);
    const [edit, setEdit] = useState(false);

    var placeHolder="";  
    var input = null;
    var fontSize = "20px"    

    //props.field   head/dish/
    //props.id      0/1/2..
    //props.type    title/description

    if(props.myContent === null || props.myContent === undefined){
        return <Loading/>
    } 

    setText(props.myContent[props.field][props.id][props.type].text);
    fontSize = props.myContent[props.field][props.id][props.type].fontSize;
    placeHolder=props.field + " " + props.type;

    if(props.type === "title")
        input = <input style={{fontSize:fontSize}} type={props.type} onChange={(event) => handleChange(event)} placeholder={placeHolder}/>

    else 
        input = <textarea style={{fontSize:fontSize}} type={props.type} onChange={(event) => handleChange(event)} placeholder={placeHolder}/>


    return<div>edit box</div>

    function handleChange(event){        
        console.log("EditBox: event.value: ", event.target.value);
        setText(event.target.value);
        console.log("EditBox: text: ", text);
    }


    function handleSubmit(event){
        props.myContent[props.field][props.id][props.type].text = text;
        props.putContent(props.myContent);
        setEdit(false)
        event.preventDefault();
    }


    function handleOnClick(){
        setEdit(true);
    }


    function handleFontSize(amount){
        const size = parseInt(fontSize.slice(0, fontSize.length-2))
        if(size < 8)
            alert("this is the minimum size");
        else if(size > 60)
            alert("this is the maximum size");
        else{
            const new_size = String(size + amount) + "px";
            props.myContent[props.field][props.id][props.type].fontSize = new_size;
            props.putContent(props.myContent);
        }
    }


    if(edit){
        return(
            <div className="row"  style={{ padding: "10px", margin:"0px"}}>
                <div  className="col-12" >
                    <form onSubmit={(event)=> handleSubmit(event)}>
                        {input}
                        <button className="edit-save btn btn-secondary" type="submit" >save</button>
                    </form>
                </div>
            </div>
        );
    }
    else{

        return(
                <div className="row"  style={{ padding: "10px", margin:"0px"}}>
                        <div  className="col-12" style={{fontSize:fontSize, mb:0}}>{text}</div> 
                        <div  className="col-12" >
                            <button className="edit-save btn btn-secondary" 
                                  onClick={()=>handleOnClick()}>edit</button>
                            <button className="edit-save-arrow btn btn-secondary" 
                                  onClick={()=>handleFontSize(+2)}>
                                <span className="fa fa-angle-up"></span>
                            </button>
                            <button className="edit-save-arrow btn btn-secondary" 
                                    onClick={()=>handleFontSize(-2)}>
                                <span className="fa fa-angle-down"></span>
                            </button>
                        </div>
                </div>
        );
    }

}

export default EditBox;
