import React, {useState, useEffect} from 'react';
import { Form, Input, Button, Label} from 'reactstrap';


const EditBox = (props) => {

    const [text, setText] = useState(null);
    const [edit, setEdit] = useState(false)

    if (props.content === null){
        return <div></div>
    }

    var field_index = 0;
    var placeHolder="";
    var type = "text";
    //var currentText = ""
    console.log("EditBox: props.content", JSON.stringify(props.content), " props.feild_name: ", props.field_name)
    switch (props.field_name){
        case "title":
            if (text === null){
                setText(props.content.title)
            }
            placeHolder="restrunt name";
            break;

        case "description":
            if (text === null){
                setText(props.content.description)
            }
            field_index = 1;
            placeHolder="restrunt description";
            type = "textarea";
    }


    function handleChange(event){        
        console.log("EditBox: event.value: ", event.target.value);
        setText(event.target.value);
        console.log("EditBox: text: ", text);
    }


    function handleSubmit(event){
        var fields = [props.content.title, props.content.description];
        fields[field_index] = text;
        console.log(fields);
        props.putContent(fields);
        event.preventDefault();
    }

    if(!setEdit){
        return(
            <Form onSubmit={(event)=> handleSubmit(event)}>
            <Label>
                {props.field_name} 
                <Input type={type} value={text} onChange={(event) => handleChange(event)}
                    placeholder={placeHolder}/>
                <Button type="submit" value="Submit" className="btn btn-small">submit</Button>
            </Label>
            </Form>
        );
    }
    else{

        return(
            <Form onSubmit={(event)=> handleSubmit(event)}>
            <Label>
                {props.field_name} 
                <Input type={type} value={text} onChange={(event) => handleChange(event)}
                    placeholder={placeHolder}/>
                <Button type="submit" value="Submit">submit</Button>
            </Label>
            </Form>
        );
    }

}

export default EditBox;






