import React from 'react';
import {
  Card,
  CardImg,
  CardBody,
  CardTitle,
  CardText,
  BreadcrumbItem,
  Breadcrumb,
  Nav,
  NavItem,
  Button,
  Modal,
  ModalBody,
  ModalHeader,
  Row,
  Label,
  Col,
} from 'reactstrap';
import { Control, LocalForm, Errors } from 'react-redux-form';
import { Link } from 'react-router-dom';

let RenderDish = ({ dish }) => {
  return (
    <Card>
      <CardImg width='100%' src={dish.image} alt={dish.alt} />
      <CardBody>
        <CardTitle>{dish.name}</CardTitle>
        <CardText>{dish.description}</CardText>
      </CardBody>
    </Card>
  );
};

const required = (val) => val && val.length;
const maxLength = (len) => (val) => !val || val.length <= len;
const minLength = (len) => (val) => val && val.length >= len;

class CommentForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isModalFormOpen: false,
    };
    this.toggleModalForm = this.toggleModalForm.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  toggleModalForm() {
    this.setState({ isModalFormOpen: !this.state.isModalFormOpen });
  }

  handleSubmit(values) {
    alert('current state is: ' + JSON.stringify(values));
    this.toggleModalForm();
  }

  render() {
    return (
      <React.Fragment>
        <Nav className='ml-auto'>
          <NavItem>
            <Button outline onClick={this.toggleModalForm}>
              <span className='fa fa-pencil fa-lg'></span> Submit Comment
            </Button>
          </NavItem>
        </Nav>
        <Modal
          isOpen={this.state.isModalFormOpen}
          toggle={this.toggleModalForm}
        >
          <ModalHeader toggle={this.toggleModalForm}>
            Submit Comment
          </ModalHeader>
          <ModalBody>
            <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
              <Row className='form-group'>
                <Label htmlFor='rating' md={12}>
                  Rating
                </Label>
                <Col md={12}>
                  <Control.select
                    model='.rating'
                    className='form-control'
                    id='rating'
                    name='rating'
                  >
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>5</option>
                  </Control.select>
                </Col>
              </Row>

              <Row className='form-group'>
                <Label htmlFor='author' md={12}>
                  Your Name
                </Label>
                <Col md={12}>
                  <Control.text
                    model='.author'
                    className='form-control'
                    id='author'
                    name='author'
                    placeholder='Your Name'
                    validators={{
                      required,
                      minLength: minLength(3),
                      maxLength: maxLength(15),
                    }}
                  />
                  <Errors
                    className='text-danger'
                    model='.yourname'
                    show='touched'
                    messages={{
                      required: 'required ',
                      minLength: 'must be greater than 2 caracters',
                      maxLength: 'must be 15 caracters or less',
                    }}
                  />
                </Col>
              </Row>

              <Row className='form-group'>
                <Label htmlFor='feedback' md={12}>
                  Comment
                </Label>
                <Col md={12}>
                  <Control.textarea
                    model='.message'
                    className='form-control'
                    id='message'
                    name='message'
                    rows='12'
                  />
                </Col>
              </Row>

              <Row className='form-group'>
                <Col md={{ size: 12 }}>
                  <Button type='submit' color='primary'>
                    Submit
                  </Button>
                </Col>
              </Row>
            </LocalForm>
          </ModalBody>
        </Modal>
      </React.Fragment>
    );
  }
}

let RenderComments = ({ comments }) => {
  if (comments != null) {
    return (
      <div className='col-12 col-md-5 m-1'>
        <h4>Comments</h4>
        <ul className='list-unstyled'>
          {comments.map(({ comment, author, date, id }) => {
            return (
              <li key={id}>
                <p> {comment} </p>

                <p>
                  --{author}, {new Date(date).toLocaleDateString()}
                </p>
              </li>
            );
          })}
        </ul>
        <CommentForm />
      </div>
    );
  } else {
    return <div></div>;
  }
};

const DishDetail = (props) => {
  if (props.dish != null) {
    return (
      <div className='container'>
        <Breadcrumb>
          <BreadcrumbItem>
            <Link to='/menu'>Menu</Link>
          </BreadcrumbItem>

          <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
        </Breadcrumb>
        <div className='col-12'>
          <h3>{props.dish.name}</h3>
          <hr />
        </div>
        <div className='row'>
          <div className='col-12 col-md-5 m-1'>
            <RenderDish dish={props.dish} />
          </div>
          <RenderComments comments={props.comments} />
        </div>
      </div>
    );
  } else {
    return <div></div>;
  }
};

export default DishDetail;
