import React, { Component } from 'react';
import {Link} from 'react-router-dom';
//for the formimport React, { Component } from 'react';
import { Breadcrumb, BreadcrumbItem, Button, Label, Col , Row} from 'reactstrap';
import { Control, Form, Errors} from 'react-redux-form';


class EditBox extends Component{

    constructor(props){
        super(props);

            /*

        this.state = {
            firstname2: '',
            lastname2: '',
            telnum2: '',
            email2: '',
            agree2: false,
            contactType2: 'Tel.',
            message2: '',
            touched2: {
                firstname2: false, 
                lastname2: false, 
                telnum2: false, 
                email2: false
            }

            */

        // necessary for using "this" in the callback
        this.handleSubmit = this.handleSubmit.bind(this);
     }

    handleSubmit(values){
        console.log("EditBox - values:" , values);
        this.props.putContent(values.text);
       // this.props.putText( values.firstname2, values.lastName2, values.telNum2, values.email2, values.agree2, values.contactType2, values.message2);

    }

    render(){
        return(
            <div className="container">
                <div className="row">
                    <div className="col-12">
                        <h3>Send us your feedback</h3>
                        <div className="col-12 col-md-9">
                            <Form model="editBox" onSubmit={(values) => this.handleSubmit(values)}>                                
                                <Row className="form-group">
                                    <Col md={10}>
                                        <Control.text model=".text" id="text" name="text" placeholder="First Name" 
                                            className="form-control" />
                                    </Col>
                                </Row>

                                <Row className="form-group">
                                    <Col md={{size: 10, offset: 2}}>
                                        <Button type="submit" color="primary">
                                            Send Feedback
                                        </Button>
                                    </Col>
                                </Row>
                            </Form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

}

export default EditBox;





