import React, {Component} from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle} from 'reactstrap';
import { Link } from 'react-router-dom';
import { Breadcrumb, BreadcrumbItem,Nav, NavItem, Button, Modal, ModalBody, ModalHeader, Label, Col , Row } from 'reactstrap'
import { Control, LocalForm, Errors} from 'react-redux-form';
import { Reducer } from '../redux/reducer';


const required = (val) => val && val.length;
const maxLength = (len) => val => !(val) || (val.length <= len)
const minLength = (len) => val => !(val) || (val.length >= len)

class DishDetail extends Component{
    constructor(props) {
        super(props);
        this.state = {
            isModalOpen: false,
        };
        this.toggleModal = this.toggleModal.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      toggleModal() {
        this.setState({ isModalOpen: !this.state.isModalOpen });
      }

      handleSubmit(values) {
        alert('current state is: ' + JSON.stringify(values));
        this.toggleModal();
      }


    render(){

        const RenderDish = (dish) => {
            return(
                <Card>
                    <CardImg width="100%" top src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
            );
        }

        const RenderComments = (comments) => {
            const comments_text = comments.map((comment)=>{
                return (
                    <li className="list-unstyled" key={comment.id}>
                    <p>
                        {comment.comment} <br/>
                        --{comment.author},
                        {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'})
                            .format(new Date(Date.parse(comment.date)))}
                    </p>
                    </li>
                );
                //we use here in a script that translate date to readble date
            });

            return(
                <div className="container">
                    <h4>comments</h4>
                    <ul className="list-gruop">
                        {comments_text}
                    </ul>
                </div>
            )
            //we use here in a script that translate date to readble date
        }

        return (
            <div className="container">
                <div className="row">
                    <Breadcrumb>

                        <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                        <BreadcrumbItem active>{this.props.dish.name}</BreadcrumbItem>
                    </Breadcrumb>
                    <div className="col-12">
                        <h3>{this.props.dish.name}</h3>
                        <hr />
                    </div>                
                </div>
                <div className="row">
                    <div className="col-12 col-md-5 m-1">
                        {RenderDish(this.props.dish)}
                    </div>
                    <div className="col-12 col-md-5 m-1">
                        {RenderComments(this.props.comments)}

                    <Nav className='ml-auto'>
                        <NavItem>
                            <Button outline onClick={this.toggleModalForm}>
                                <span className='fa fa-pencil fa-lg'></span> Submit Comment
                            </Button>
                        </NavItem>
                    </Nav>
                    </div>
                </div>

        <Modal
          isOpen={this.state.isModalOpen}
          toggle={this.toggleModal}
        >
          <ModalHeader toggle={this.toggleModal}>
            Submit Comment
          </ModalHeader>
          <ModalBody>
            <LocalForm onSubmit={(values) => this.handleSubmit(values)}>
              <Row className='form-group'>
                <Label htmlFor='rating' md={12}>
                  Rating
                </Label>
                <Col md={12}>
                  <Control.select
                    model='.rating'
                    className='form-control'
                    id='rating'
                    name='rating'
                  >
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>5</option>
                  </Control.select>
                </Col>
              </Row>

              <Row className='form-group'>
                <Label htmlFor='author' md={12}>
                  Your Name
                </Label>
                <Col md={12}>
                  <Control.text
                    model='.author'
                    className='form-control'
                    id='author'
                    name='author'
                    placeholder='Your Name'
                    validators={{
                      required,
                      minLength: minLength(3),
                      maxLength: maxLength(15),
                    }}
                  />
                  <Errors
                    className='text-danger'
                    model='.yourname'
                    show='touched'
                    messages={{
                      required: 'required ',
                      minLength: 'must be greater than 2 caracters',
                      maxLength: 'must be 15 caracters or less',
                    }}
                  />
                </Col>
              </Row>

              <Row className='form-group'>
                <Label htmlFor='feedback' md={12}>
                  Comment
                </Label>
                <Col md={12}>
                  <Control.textarea
                    model='.message'
                    className='form-control'
                    id='message'
                    name='message'
                    rows='12'
                  />
                </Col>
              </Row>

              <Row className='form-group'>
                <Col md={{ size: 12 }}>
                  <Button type='submit' color='primary'>
                    Submit
                  </Button>
                </Col>
              </Row>
            </LocalForm>
          </ModalBody>
        </Modal>
            </div>
        );
    }
}


export default DishDetail;
