import React, {Component } from 'react';
import {Navbar, NavbarBrand, Nav, NavbarToggler, Collapse, NavItem, Jumbotron,
     Button, Modal, ModalHeader, ModalBody, Form, FormGroup, Input, Label} from 'reactstrap'
// nav link auto maticly impliment the active component and a tag
import {NavLink} from 'react-router-dom';
import EditBox from './EditBoxComponent';


class Header extends Component{

    constructor(props){
        super(props);
        this.state = {
            isNavOpen: false,
            isModalOpen: false,
            tab: "login"
        };
        // this.toggleNav used in the NavbarToggler onClick is know declare to point to the function
        this.toggleModal = this.toggleModal.bind(this);
        this.toggleNav = this.toggleNav.bind(this);
        this.handleLogin = this.handleLogin.bind(this);

    }

    

    toggleModal() {
        this.setState({
          isModalOpen: !this.state.isModalOpen
        });
      }

    toggleNav(){
        this.setState({
        isNavOpen: !this.state.isNavOpen
        })
    }
    handleLogin(event){
        
        var error = "";
        username = this.props.content.filter((user) => user.id === this.username.value);
        if(username.length > 0){
            username = [0];
            if(username.password === this.password.value){
                //login
            }
            else{
                //wrong password
                error = "wrong password or user name"
            }
        }
        else{
            //create new user?

        }
        const fields = [this.username.value, this.password.value, this.props.content.title, this.props.content.titleFontSize, 
                        this.props.content.description , this.props.content.descriptionFontSize];

        this.props.putContent(fields);
        this.toggleModal();
        
        event.preventDefault();

    }

    render(){
        
        console.log("HeaderComponent: render, content", this.props.content)
        var title = ""
        var description = ""

            if(this.props.content !== null){
                console.log("HeaderComponent, if, content", this.props.content)
                title = this.props.content.title
                description = this.props.content.description
            }

  
        return(
            <React.Fragment>
                <Navbar dark expand="md">
                    <div className="container">
                        <NavbarToggler onClick={this.toggleNav}/>
                        <NavbarBrand className='mr-auto' href="/">
                            <img src='assets/images/logo.png' height="30" width="41"
                                alt={title}/>
                        </NavbarBrand>
                        <Collapse isOpen={this.state.isNavOpen} navbar>
                            <Nav navbar>
                                <NavItem>
                                    <NavLink className="nav-link ml-md-5 offset-2 offset-md-0" to="/home">
                                        <span className="fa fa-home"></span> Home
                                    </NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink className="nav-link offset-2 offset-md-0" to="/aboutus">
                                        <span className="fa fa-info"></span> About Us
                                    </NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink className="nav-link offset-2 offset-md-0" to="/menu">
                                        <span className="fa fa-list"></span> Menu
                                    </NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink className="nav-link offset-2 offset-md-0" to="/contactus">
                                        <span className="fa fa-address-card"></span> Contact US
                                    </NavLink>
                                </NavItem>
                            </Nav>
                            <Nav className="ml-auto" navbar>
                                <NavItem >
                                    <Button outline onClick={this.toggleModal} className="nav-link offset-2 offset-md-0"><span className="fa fa-sign-in"></span> Login</Button>
                                </NavItem>
                            </Nav>
                        </Collapse>
                    </div>
                </Navbar>
                <Jumbotron>
                    <div className="container">
                        <div className='row row-header'>
                            <div className='col-12 col-sm-6'>
                                <EditBox field_name="title" putContent ={ this.props.putContent} content={this.props.content}/>
                                <EditBox field_name="description" putContent ={ this.props.putContent} content={this.props.content}/>
                            </div>
                        </div>
                    </div>
                </Jumbotron>
                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Login</ModalHeader>
                    <ModalBody>
                    <Form onSubmit={this.handleLogin}>
                            <FormGroup>
                                <Label htmlFor="username">Username</Label>
                                <Input type="text" id="username" name="username"
                                    innerRef={(input) => this.username = input} />
                            </FormGroup>
                            <FormGroup>
                                <Label htmlFor="password">Password</Label>
                                <Input type="password" id="password" name="password"
                                    innerRef={(input) => this.password = input}  />
                            </FormGroup>
                            <FormGroup check>
                                <Label check>
                                    <Input type="checkbox" name="remember"
                                    innerRef={(input) => this.remember = input}  />
                                    Remember me
                                </Label>
                            </FormGroup>
                            <Button type="submit" value="submit" color="primary">Login</Button>
                        </Form>
                    </ModalBody>
                </Modal>
            </React.Fragment>
        );
    }

}

export default Header
